import React, { useEffect, useState } from "react";
import "./BlogList.css";

export default function BlogList() {
  const [posts, setPosts] = useState([]);
  const [editPost, setEditPost] = useState(null);

  const fetchPosts = async () => {
    const res = await fetch("http://localhost:5000/api/posts");
    const data = await res.json();
    setPosts(data);
  };

  useEffect(() => {
    fetchPosts();
  }, []);

  const handleDelete = async (id) => {
    await fetch(`http://localhost:5000/api/posts/${id}`, { method: "DELETE" });
    fetchPosts();
  };

  const handleEdit = (post) => {
    setEditPost(post);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    await fetch(`http://localhost:5000/api/posts/${editPost.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(editPost),
    });
    setEditPost(null);
    fetchPosts();
  };

  const handleChange = (e) => {
    setEditPost({ ...editPost, [e.target.name]: e.target.value });
  };

  return (
    <div className="blog-list">
      <h2>Blog Posts</h2>
      {editPost && (
        <form onSubmit={handleUpdate} className="edit-form">
          <input name="title" value={editPost.title} onChange={handleChange} />
          <textarea name="content" value={editPost.content} onChange={handleChange} />
          <input type="date" name="date" value={editPost.date} onChange={handleChange} />
          <input name="author" value={editPost.author} onChange={handleChange} />
          <button type="submit">Update</button>
          <button onClick={() => setEditPost(null)}>Cancel</button>
        </form>
      )}
      <ul>
        {posts.map((post) => (
          <li key={post.id}>
            <h3>{post.title}</h3>
            <p>{post.content}</p>
            <small>By {post.author} on {post.date}</small>
            <div className="btn-group">
              <button onClick={() => handleEdit(post)}>Edit</button>
              <button onClick={() => handleDelete(post.id)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
