import React, { useState } from "react";
import "./CreatePost.css";

export default function CreatePost() {
  const [formData, setFormData] = useState({
    title: "",
    content: "",
    date: "",
    author: ""
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch("http://localhost:5000/api/posts", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData)
    });

    const data = await response.json();
    alert(data.message);
  };

  return (
    <div className="create-post-container">
      <h2>Create New Blog Post</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="title" placeholder="Title" required onChange={handleChange} />
        <textarea name="content" placeholder="Content" required onChange={handleChange}></textarea>
        <input type="date" name="date" required onChange={handleChange} />
        <input type="text" name="author" placeholder="Author" required onChange={handleChange} />
        <button type="submit">Upload</button>
      </form>
      <a href="/" className="back-link">Back to Blog</a>
    </div>
  );
}
