import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./Travellist.css";

export default function Travellist() {
  const [posts, setPosts] = useState([]);
  const [comments, setComments] = useState({});

  useEffect(() => {
    fetch("http://localhost:5000/api/posts")
      .then((res) => res.json())
      .then(setPosts)
      .catch((err) => console.error("Failed to fetch posts:", err));
  }, []);

  const handleCommentChange = (e, postId) => {
    setComments({ ...comments, [postId]: e.target.value });
  };

  const handleCommentSubmit = (postId) => {
    alert(`Comment on post ${postId}: ${comments[postId]}`);
    setComments({ ...comments, [postId]: "" });
  };

  return (
    <div className="blog-list">
      <h2>Blog Posts</h2>
      <Link to="/createpost" className="create-button">Create New Blog</Link>

      {posts.length === 0 ? (
        <p>Loading or no posts available.</p>
      ) : (
        <ul>
          {posts.map(({ id, title, content, author, date, category }) => (
            <li key={id}>
              <h3>{title}</h3>
              <p>{content}</p>
              <small>By {author} on {date}</small>
              <p><strong>Category:</strong> {category}</p>

              {/* Comment section */}
              <div className="comment-section">
                <textarea
                  placeholder="Write a comment..."
                  value={comments[id] || ""}
                  onChange={(e) => handleCommentChange(e, id)}
                />
                <button onClick={() => handleCommentSubmit(id)}>Submit Comment</button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
